import { Injectable } from '@angular/core';
import { HttpClient, HttpParams } from '@angular/common/http';
import { Observable, of } from 'rxjs';
import { catchError } from 'rxjs/operators';
import { Quote } from '../models/quote.model';

@Injectable({
    providedIn: 'root'
  })

export class QuoteService{
    private readonly API_URL = 'https://api.quotable.io/search/quotes';

    constructor(private http: HttpClient) {};

    searchQuotes(searchTerm: string): Observable<Quote[]>{
        const params = new HttpParams().set('query', searchTerm);

        return this.http.get<Quote[]>(this.API_URL, { params })
            .pipe(
                catchError(this.handleError<Quote[]> ('searchQuotes', []))
            );
    }

    private handleError<T>(operation = 'operation', result?: T){
        return (error: any): Observable<T> =>{
            console.error(`${operation} failed: ${error.message}`);
            return of(result as T);
        }
    }

}
